data.value:
;WITH items AS ( select i.nombre, d.valorpagado
from te_mediospagotiquete d inner join te_tiquetes c on d.idtiquete = c.id inner join te_mediospago i on d.Idmedio = i.id where c.Fecha >= @fi and c.Fecha <= @ff and c.Cancelada = 0 and c.Idusuario = @iduaurio and c.Caja = @caja )
select c.nombre, count(*) Transacciones, Sum(c.Valorpagado) Venta from items c group by c.nombre
